import React, { Component, Fragment } from "react";
import axios from "axios";
import { Container, Row, Col} from "reactstrap";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faPlus } from "@fortawesome/free-solid-svg-icons";
import Nav from "../../organisms/Nav";
import Header from "../../organisms/Header";
import Form from "../../molecules/Form";
import SelectReact from "../../atoms/Select";
import { Button } from "react-bootstrap";
import styles from "./Tasks.scss";

export default class Tasks extends Component {

	constructor(props) {
		super(props);
		this.state = {
			isLoading: true,
			displayForm: false,
			tasks: []
		}
	}

	componentDidMount() {

		axios({
			method: "GET",
			url: "/tasks"
		})
		.then(res => {
			this.setState({
				isLoading: false,
				tasks: [] // change to res.BLANK
			});
		})
		.catch(err => {
			throw err;
		});
	}

	componentDidUpdate() {

	}

	render() {
		let { displayForm } = this.state;

		if (displayForm === false) {
			return (
				<Fragment>
					<Nav />
					<Container id="tasks">
						<Row className="header">
							<Col>
								<Header header="Tasks"/>
							</Col>
							<Col>
								<Button href="/tasks/create"><FontAwesomeIcon icon={faPlus} /> New Task</Button>
							</Col>
						</Row> <hr/>
						<Row>	
							<Col>
								<SelectReact placeholder="Filter by Course.."/>
							</Col>
							<Col>
								<Button href="/tasks" className="current">Current</Button>
								<Button href="/tasks/past" className="past">Past</Button>
							</Col>
							<hr/>
						</Row>
					</Container>
				</Fragment>
			)
		} else {
			return (
				<Fragment>
					<Nav />
					<Container id="tasks">
						<Row className="header">
							<Col>
								<Header header="Tasks"/>
							</Col>
							<Col>
								<Button href="/tasks/newTask"><FontAwesomeIcon icon={faPlus} /> New Task</Button>
							</Col>
						</Row> <hr/>
						<Row>
							<Col>
								<Row>
									<Col>
										<SelectReact placeholder="Filter by Course.."/>
									</Col>
									<Col>
										<Button href="/tasks" className="current">Current</Button>
										<Button href="/tasks/past" className="past">Past</Button>
									</Col>
								</Row>
								<hr/>
							</Col>
							<Col>
								<Form 
									header="Task" 
									action="/newTask" 
									page="/tasks"
									course="tasksCourse"
									module="tasksModule"
									deadline="tasksDeadline"
									type="tasksType"
									notes="tasksNotes"
								/>
							</Col>
						</Row>
					</Container>
				</Fragment>
			)
		}
	}
}
